import React, {useState} from 'react'
import './List.css';

const List = () => {
  let list = [
    {id:1, title: '노트르담드파리', stage: '우리집'},
    {id:2, title: '캣츠', stage: '승원이집'},
    {id:3, title: '준장(진) 이강희의 재림', stage: '체계단'}
  ];
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredMusicals = list.filter((musical) =>
    musical.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="musical-list">
      <input
        type="text"
        className="search-bar"
        placeholder="Search musicals..."
        value={searchTerm}
        onChange={handleSearchChange}
      />
      <ul>
        {filteredMusicals.map((musical) => (
          <li key={musical.id}>{musical.title} {musical.stage}</li>
        ))}
      </ul>
    </div>
  );
};

export default List;
