import React from "react";
import Slider from "react-slick";
import "./Slider.css";

export default function MySlider() {
  var settings = {
    dots: true, // 사진 밑에 뜨는 동그라미 적용 여부 (true/false)
    infinite: true, // 슬라이드 반복 여부. true일 경우 마지막 슬라이드 다음이 다시 처음 슬라이드가 된다. (true/false)
    speed: 500, // 슬라이드 넘어가는 속도 (ms)
    slidesToShow: 1, // 한 번에 볼 수 있는 슬라이드의 개수
    slidesToScroll: 1, //  한 번에 넘어가는 슬라이드의 수
    arrows: false, // 슬라이드 양 옆에 뜨는 화살표
  };
  const images = [
    'https://via.placeholder.com/800x400?text=Image+1',
    'https://via.placeholder.com/800x400?text=Image+2',
    'https://via.placeholder.com/800x400?text=Image+3',
  ];

  return (
    <div className="slider-container">
      <Slider {...settings}>
        {images.map((img, index) => (
          <div key={index}>
            <img src={img} alt={`Slide ${index + 1}`} />
          </div>
        ))}
      </Slider>
    </div>
  );
};
