import React, { useState } from 'react';
import './List.css';

const musicals = [
  {
    id: 1,
    name: 'Ï§ÄÏû• Ïù¥Í∞ïÌù¨Ïùò Ïû¨Î¶º',
    stage: 'Ï≤¥Í≥ÑÎã®',
    period: '2024.01.01 - 2024.03.31',
    image: 'https://via.placeholder.com/60'
  },
  {
    id: 2,
    name: 'Les Mis√©rables',
    stage: 'West End',
    period: '2024.02.01 - 2024.05.31',
    image: 'https://via.placeholder.com/60'
  },
  {
    id: 3,
    name: 'Hamilton',
    stage: 'Victoria Palace Theatre',
    period: '2024.03.01 - 2024.06.30',
    image: 'https://via.placeholder.com/60'
  },
  // Îçî ÎßéÏùÄ ÎÆ§ÏßÄÏª¨ Îç∞Ïù¥ÌÑ∞ Ï∂îÍ∞Ä
];

const List = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const clearSearch = () => {
    setSearchTerm('');
  };

  const filteredMusicals = musicals.filter((musical) =>
    musical.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="list-container">
      <div className="search-container">
        <span className="search-icon">üîç</span>
        <input
          type="text"
          className="search-bar"
          placeholder="Search musicals..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
        {searchTerm && <button className="clear-button" onClick={clearSearch}>√ó</button>}
      </div>
      <ul className="list">
        {filteredMusicals.map((musical) => (
          <li key={musical.id} className="list-item">
            <img src={musical.image} alt={musical.name} />
            <div className="list-item-info">
              <div className="list-item-title">{musical.name}</div>
              <div className="list-item-stage">Stage: {musical.stage}</div>
              <div className="list-item-period">Period: {musical.period}</div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default List;
