import React, { useState } from 'react';
import './List.css';

const musicals = [
  {
    id: 1,
    name: 'Ï§ÄÏû• Ïù¥Í∞ïÌù¨Ïùò Ïû¨Î¶º',
    stage: 'Ï≤¥Í≥ÑÎã®',
    period: '2024.01.01 - 2024.10.31',
    image: 'https://via.placeholder.com/150'
  },
  {
    id: 2,
    name: 'ÎßåÍ∏∞Ï†ÑÏó≠Ìïú ÎÇ¥Í∞Ä Ïù¥Î≥ë?',
    stage: 'Ï≤¥Í≥ÑÎã®',
    period: '2024.08.01 - 2026.5.31',
    image: 'https://via.placeholder.com/150'
  },
  {
    id: 3,
    name: 'Hamilton',
    stage: 'Victoria Palace Theatre',
    period: '2024.03.01 - 2024.06.30',
    image: 'https://via.placeholder.com/150'
  },
  {
    id: 4,
    name: '..',
    stage: 'Ïö∞Î¶¨Ïßë',
    period: '2024.03.01 - 2024.06.30',
    image: 'https://via.placeholder.com/150'
  },
  // Ïã§Ï†úÎ°úÎäî DBÏóêÏÑú Í∞ÄÏ†∏Ïò§Í∏∞.
];

const List = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('all'); // ÌïÑÌÑ∞ ÏÉÅÌÉú Ï∂îÍ∞Ä

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const clearSearch = () => {
    setSearchTerm('');
  };

  const handleFilterChange = (filter) => {
    setFilter(filter);
  };

  const currentDate = new Date();
  const filteredMusicals = musicals.filter((musical) => {
    const musicalEndDate = new Date(musical.period.split(' - ')[1]);
    const matchesSearchTerm = musical.name.toLowerCase().includes(searchTerm.toLowerCase());

    if (filter === 'current') {
      return matchesSearchTerm && musicalEndDate >= currentDate;
    }

    if (filter === 'past') {
      return matchesSearchTerm && musicalEndDate < currentDate;
    }

    return matchesSearchTerm;
  });

  return (
    <div className="list-container">
      <div className="search-container">
        {/* <span className="search-icon">üîç</span> */}
        <input
          type="text"
          className="search-bar"
          placeholder="Search musicals..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
        {searchTerm && <button className="clear-button" onClick={clearSearch}>√ó</button>}
        <button className="filter-button" onClick={() => handleFilterChange('all')}>All</button>
        <button className="filter-button" onClick={() => handleFilterChange('current')}>Current</button>
        <button className="filter-button" onClick={() => handleFilterChange('past')}>Past</button>
      </div>
      <ul className="list">
        {filteredMusicals.map((musical) => (
          <li key={musical.id} className="list-item">
            <img src={musical.image} alt={musical.name} />
            <div className="list-item-info">
              <div className="list-item-title">{musical.name}</div>
              <div className="list-item-stage">{musical.stage}</div>
              <div className="list-item-period">{musical.period}</div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default List;
