import React, {useState} from 'react'
import './List.css';

const List = () => {
  let list = [
    {id:1, title: 'ÎÖ∏Ìä∏Î•¥Îã¥ÎìúÌååÎ¶¨', stage: 'Ïö∞Î¶¨Ïßë', period: '2024.1.1.~'},
    {id:2, title: 'Ï∫£Ï∏†', stage: 'ÏäπÏõêÏù¥Ïßë'},
    {id:3, title: 'Ï§ÄÏû•(ÏßÑ) Ïù¥Í∞ïÌù¨Ïùò Ïû¨Î¶º', stage: 'Ï≤¥Í≥ÑÎã®'}
  ];
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const clearSearch = () => {
    setSearchTerm('');
  };

  const filteredMusicals = list.filter((musical) =>
    musical.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="musical-list">
      <div className="search-container">
        <span className="search-icon">üîç</span>
        <input
          type="text"
          className="search-bar"
          placeholder="Search musicals..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
        {searchTerm && <button className="clear-button" onClick={clearSearch}>√ó</button>}
      </div>
      <ul>
        {filteredMusicals.map((musical) => (
          <li key={musical.id}>{musical.title}{musical.stage}</li>
        ))}
      </ul>
    </div>
  );
};

export default List;
